mapper.update:
UPDATE EMPLOYEES
   SET EMAIL = #{email}
   	 , JOB_ID = #{jobId}
   	 , DEPARTMENT_ID = #{deptId}
   	 , FIRST_NAME = SUBSTR(#{empName}, 1, INSTR(#{empName}, ' ') - 1)
   	 , LAST_NAME = SUBSTR(#{empName}, INSTR(#{empName}, ' ') + 1, LENGTH(#{empName}))
 WHERE EMPLOYEE_ID = #{empId}

UPDATE EMPLOYEES
   SET PHONE_NUMBER = #{phone}
   	 , HIRE_DATE = #{hireDate}
   	 , SALARY = #{salary}
   	 , COMMISSION_PCT = #{commission}
 WHERE EMPLOYEE_ID = #{empId}
